    public function copyWithFixed(ApiKeyClientAttributes $attributes, ?ApiKeyClient $original = null): ApiKeyClientAttributes
    {
        $this->logDebug("Copy with fixed for Api key client");
        $span = $this->startSpan("Copy with fixed for  Api key client");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new ApiKeyClientPresetValues($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(ApiKeyClientAttributes $attributes, ?ApiKeyClient $original = null): ApiKeyClientAttributes
    {
        $this->logDebug("Copy with hidden for Api key client");
