    public function retrieveVisible(ApiKeyClientRef $ref): ?ApiKeyClient
    {
        $this->logDebug("Retrieve visible for Api key client");
        $span = $this->startSpan("Retrieve visible for  Api key client");
        try {
            $filter = new ApiKeyClientFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
