<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ApiKeyClient\Application\Usecase\Delete;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\ApiKeyClient\Domain\ApiKeyClientRef;
use Civi\Lughauth\Features\Access\ApiKeyClient\Domain\ApiKeyClient;
use Civi\Lughauth\Features\Access\ApiKeyClient\Application\Service\Visibility\ApiKeyClientVisibilityService;
use Civi\Lughauth\Features\Access\ApiKeyClient\Domain\Gateway\ApiKeyClientWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class ApiKeyClientDeleteUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly ApiKeyClientVisibilityService $visibility,
        private readonly ApiKeyClientWriteGateway $writer
    ) {
    }

    public function allowDelete(?ApiKeyClientRef $ref = null): Allow
    {
        $this->logDebug("Check allow of delete usecase for Api key client");
        $span = $this->startSpan("Check allow of delete usecase for Api key client");
        try {
            $result = $this->dispacher->dispatch(new ApiKeyClientDeleteAllowProposal(Allow::allowed('delete', 'Allowed to delete by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function delete(string $uid): void
    {
        $this->logDebug("Run delete usecase for Api key client");
        $span = $this->startSpan("Run delete usecase for Api key client");
        try {
            $ref = new ApiKeyClientRef($uid);
            $allow = $this->allowDelete($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new ApiKeyClientDeleteInputProposal($ref));
            if (!$original = $this->visibility->retrieveVisibleForUpdate($input->ref)) {
                throw new NotFoundException($uid);
            }
            $deleted = $original->delete();
            $this->writer->delete($deleted);
            $this->dispacher->dispatch(new ApiKeyClientDeleteOuputProposal($ref));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
