namespace Civi\Lughauth\Features\Access\ApiKeyClient\Application\Usecase\Update;

use RuntimeException;
use Civi\Lughauth\Features\Access\ApiKeyClient\Domain\ApiKeyClient;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class ApiKeyClientUpdateCheck
{
    public function __construct(public readonly ApiKeyClientUpdateParams $input, public readonly ApiKeyClient $current)
    {
    }
    public function reject(RuntimeException|ConstraintFail $fail)
    {
        if ($fail instanceof ConstraintFail) {
            throw ConstraintException::ofFail($fail);
        } else {
            throw $fail;
        }
    }
}
