<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ApiKeyClient\Application\Usecase\Update;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\ApiKeyClient\Domain\ApiKeyClientRef;
use Civi\Lughauth\Features\Access\ApiKeyClient\Domain\ApiKeyClientAttributes;
use Civi\Lughauth\Features\Access\ApiKeyClient\Application\Service\Visibility\ApiKeyClientVisibilityService;
use Civi\Lughauth\Features\Access\ApiKeyClient\Domain\Gateway\ApiKeyClientWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class ApiKeyClientUpdateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly ApiKeyClientVisibilityService $visibility,
        private readonly ApiKeyClientWriteGateway $writer
    ) {
    }

    public function allowUpdate(?ApiKeyClientRef $ref = null): Allow
    {
        $this->logDebug("Check allow update usecase for Api key client");
        $span = $this->startSpan("Check allow update usecase for Api key client");
        try {
            $result = $this->dispacher->dispatch(new ApiKeyClientUpdateAllowDecision(Allow::allowed('update', 'Allowed to update by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fixedFields(?ApiKeyClientRef $ref = null): array
    {
        $this->logDebug("Check fixed fields for update usecase for Api key client");
        $span = $this->startSpan("Check fixed fields for update usecase for Api key client");
        try {
            return $this->visibility->fieldsToFix($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function update(string $uid, ApiKeyClientAttributes $attributes): ApiKeyClientAttributes
    {
        $this->logDebug("Run update usecase for Api key client");
        $span = $this->startSpan("Run update usecase for Api key client");
        try {
            $ref = new ApiKeyClientRef($uid);
            $allow = $this->allowUpdate($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $input = $this->visibility->copyWithFixed($this->dispacher->dispatch(new ApiKeyClientUpdateInputProposal($ref, $attributes))->attributes);
            $modified = $original->replace($input);
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($result->toAttributes());
            return $this->dispacher->dispatch(new ApiKeyClientUpdateOutputProposal($output))->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
