<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ApiKeyClient\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\ApiKeyClient\Domain\ValueObject\ApiKeyClientCodeVO;

trait ApiKeyClientCodeAccesor
{
    private ApiKeyClientCodeVO $_code;
    public function getCode(): string
    {
        return $this->_code->value();
    }
    public function code(): ApiKeyClientCodeVO
    {
        return $this->_code;
    }
    public function isCodeChanged(?self $original = null): bool
    {
        return $this->_code->value() !== ($original ? $original->_code->value() : null);
    }
    public function withCode(ApiKeyClientCodeVO|string $code): self
    {
        $copy = clone $this;
        $copy->_code = ApiKeyClientCodeVO::from($code);
        return $copy;
    }
}
