<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ApiKeyClient\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\ApiKeyClient\Domain\ValueObject\ApiKeyClientKeyVO;

trait ApiKeyClientKeyAccesor
{
    private ?ApiKeyClientKeyVO $_key = null;
    public function getKey(): ?string
    {
        return $this->_key?->value();
    }
    public function key(): ?ApiKeyClientKeyVO
    {
        return $this->_key;
    }
    public function isKeyChanged(?self $original = null): bool
    {
        return $this->_key->value() !== ($original ? $original->_key->value() : null);
    }
    public function withKey(ApiKeyClientKeyVO|string|null $key): self
    {
        $copy = clone $this;
        $copy->_key = ApiKeyClientKeyVO::from($key);
        return $copy;
    }
}
