    {
        if ($ref instanceof ApiKeyClient) {
            return $ref;
        } elseif (!$ref->_private_resolve) {
            $ref->_private_resolve = $this->conn->retrieve(new ApiKeyClientFilter(uids: [ $ref->uid() ]));
