            $this->changelog->recordDeletion('api-key-client', $entity->uid(), $entity->asPublicJson());
            return $result;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
