<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\ApiKeyClient\Application\Usecase\Retrieve;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\Master\ApiKeyClient\Domain\ApiKeyClientRef;
use Civi\Lughauth\Features\Access\Master\ApiKeyClient\Application\Service\Visibility\ApiKeyClientVisibilityService;
use Civi\Lughauth\Features\Access\Master\ApiKeyClient\Domain\Gateway\ApiKeyClientReadGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class ApiKeyClientRetrieveUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly ApiKeyClientVisibilityService $visibility,
        private readonly ApiKeyClientReadGateway $reader
    ) {
    }

    public function allowRetrieve(?ApiKeyClientRef $ref = null): Allow
    {
        $this->logDebug("Check allow of retrieve usecase for Api key client");
        $span = $this->startSpan("Check allow of retrieve usecase for Api key client");
        try {
            $result = $this->dispacher->dispatch(new ApiKeyClientRetrieveAllowDecision(Allow::allowed('retrieve', 'Allowed to retrieve by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function hiddenFields(?ApiKeyClientRef $ref = null): array
    {
        $this->logDebug("Check hidden fields usecase for Api key client");
        $span = $this->startSpan("Check hidden fields usecase for Api key client");
        try {
            return $this->visibility->fieldsToHide($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieve(string $uid): ApiKeyClientRetrieveResult
    {
        $this->logDebug("Run retrieve usecase for Api key client");
        $span = $this->startSpan("Run retrieve usecase for Api key client");
        try {
            $ref = new ApiKeyClientRef($uid);
            $allow = $this->allowRetrieve($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$result = $this->visibility->retrieveVisible($ref)) {
                throw new NotFoundException($uid);
            }
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new ApiKeyClientRetrieveResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
