<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\ApiKeyClient\Domain\Gateway;

use Civi\Lughauth\Features\Access\Master\ApiKeyClient\Domain\ApiKeyClient;

class ApiKeyClientCursor
{
    public function __construct(
        private int $limit,
        private ?string $sinceUid = null,
    ) {
    }
    public function next(ApiKeyClient $apiKeyClient, ?int $size): ApiKeyClientCursor
    {
        $next = clone $this;
        $next->sinceUid = $apiKeyClient->uid();
        $next->limit = $size ?? $this->limit;
        return $next;
    }
    public function limit(): int
    {
        return $this->limit;
    }
    public function sinceUid(): ?string
    {
        return $this->sinceUid;
    }
}
