        $this->logDebug("Check generic allow for Api key client");
        $span = $this->startSpan("Generic allow for Api key client");
        try {
            $value = [
              'allows' => [
                $this->createUsecase->allowCreate(),
                $this->listUsecase->allowList(),
                $this->updateUsecase->allowUpdate(),
              $this->deleteUsecase->allowDelete(),
