<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\ApiKeyClient\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Shared\Infrastructure\LongTask\Runner;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskKey;
use Civi\Lughauth\Features\Access\Master\ApiKeyClient\Application\Usecase\Delete\ApiKeyClientDeleteUsecase;
use Civi\Lughauth\Features\Access\Master\ApiKeyClient\Domain\Gateway\ApiKeyClientFilter;
use Civi\Lughauth\Features\Access\Master\ApiKeyClient\Infrastructure\Driver\Batch\ApiKeyClientTaskDelete;

class ApiKeyClientDeleteController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SqlTemplate $sql,
        private readonly Runner $runner,
        private readonly ApiKeyClientDeleteUsecase $deleteUsecase,
    ) {
    }
    #[OA\Delete(
        path: "/api/access/api-key-clients/{uid}",
        tags: ["Api key client"],
        description: "Delete the record",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        responses: [
        new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/ApiKeyClientApiDTO")),
        new OA\Response(response: 404, description: "Not found"),
    ]
    )
    ]
    public function delete(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Delete Api key client");
        $span = $this->startSpan("Delete Api key client");
        $this->sql->begin();
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $this->deleteUsecase->delete($uid);
            $this->sql->commit();
            return $response->withStatus(204)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            ;
            $span->end();
        }
    }
    public function deleteAllForQuery(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Delete all for query Api key client");
        $span = $this->startSpan("Delete all for query Api key client");
        try {
            $params = $request->getQueryParams();
            $filter = new ApiKeyClientFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                code: $params['code'] ?? null,
                key: $params['key'] ?? null,
            );
            $res = $this->runner->run(ApiKeyClientTaskDelete::class, ['filter' => $filter]);
            $response->getBody()->write(json_encode($res));
            return $response;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function checkDeleteAllForQueryStatus(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check delete all for query Api key client");
        $span = $this->startSpan("Check delete all for query Api key client");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $info = $this->runner->detail(new TaskKey($uid));
            if (!$info) {
                throw new NotFoundException('-');
            }
            $response->getBody()->write(json_encode($info));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
