<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\RelyingParty\Application\Policy\Allow\Delete;

use Throwable;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Application\Usecase\Delete\RelyingPartyDeleteAllowDecision;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class DeleteRelyingPartyOnlyForRootAllow
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(RelyingPartyDeleteAllowDecision $proposal): RelyingPartyDeleteAllowDecision
    {
        $this->logDebug("Check DeleteRelyingPartyOnlyForRootAllow Relying party");
        $span = $this->startSpan("Check DeleteRelyingPartyOnlyForRootAllow Relying party");
        try {
            $userContext = $this->context->getIdentity();
            if ($proposal->isAllowed()) {
                if (! $userContext->hasAnyRole('root:admin')) {
                    $proposal->deny('Disabled if not DeleteRelyingPartyOnlyForRoot');
                }
            }
            return $proposal;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
