<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\RelyingParty\Application\Usecase\Update;

use RuntimeException;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\RelyingParty;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class RelyingPartyUpdateCheck
{
    public function __construct(public readonly RelyingPartyUpdateParams $input, public readonly RelyingParty $current)
    {
    }
    public function reject(RuntimeException|ConstraintFail $fail)
    {
        if ($fail instanceof ConstraintFail) {
            throw ConstraintException::ofFail($fail);
        } else {
            throw $fail;
        }
    }
}
