<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\ValueObject\RelyingPartySchemasVO;

trait RelyingPartySchemasAccesor
{
    private RelyingPartySchemasVO $_schemas;
    public function getSchemas(): string
    {
        return $this->_schemas->value();
    }
    public function schemas(): RelyingPartySchemasVO
    {
        return $this->_schemas;
    }
    public function isSchemasChanged(?self $original = null): bool
    {
        return $this->_schemas->value() !== ($original ? $original->_schemas->value() : null);
    }
    public function withSchemas(RelyingPartySchemasVO|string $schemas): self
    {
        $copy = clone $this;
        $copy->_schemas = RelyingPartySchemasVO::from($schemas);
        return $copy;
    }
}
