<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\ValueObject\Holder;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\ValueObject\RelyingPartyPoliciesVO;

trait RelyingPartyPoliciesAttributeHolder
{
    protected RelyingPartyPoliciesVO|string|null $policies = null;
    protected bool $policiesAssigned = false;

    public function getPoliciesOrDefault(?RelyingPartyPoliciesVO $policies): ?RelyingPartyPoliciesVO
    {
        return $this->policiesAssigned ? ($this->policies !== null ? RelyingPartyPoliciesVO::from($this->policies) : null) : $policies;
    }
    public function policies(RelyingPartyPoliciesVO|string|null $policies): static
    {
        $this->policies = $policies;
        $this->policiesAssigned = true;
        return $this;
    }
    public function getPolicies(): ?string
    {
        return is_a($this->policies, RelyingPartyPoliciesVO::class) ? $this->policies->value() : $this->policies;
    }
    public function unsetPolicies(): static
    {
        $this->policies = null;
        $this->policiesAssigned = false;
        return $this;
    }
    protected function withDefaultPolicies(): void
    {
    }
    protected function withAssertedPoliciesRules(self $value, ConstraintFailList $errorsList): void
    {
        if ($value->policiesAssigned = $this->policiesAssigned) {
            $value->policies = RelyingPartyPoliciesVO::tryFrom($this->policies, $errorsList);
        }
    }
}
