<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\RelyingParty\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Application\Usecase\Update\RelyingPartyUpdateUsecase;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\ValueObject\RelyingPartyUidVO;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\ValueObject\RelyingPartyCodeVO;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\ValueObject\RelyingPartyApiKeyVO;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\ValueObject\RelyingPartyPoliciesVO;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\ValueObject\RelyingPartyVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Application\Usecase\Update\RelyingPartyUpdateParams;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Application\Usecase\Update\RelyingPartyUpdateResult;

class RelyingPartyUpdateController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SqlTemplate $sql,
        private readonly RelyingPartyUpdateUsecase $updateUsecase,
    ) {
    }
    #[OA\Put(
        path: "/api/access/relying-parties/{uid}",
        tags: ["Relying party"],
        description: "Update the value",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        requestBody: new OA\RequestBody(content: new OA\JsonContent(type: "object", ref:"#/components/schemas/RelyingPartyApiDTO")),
        responses: [
        new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/RelyingPartyApiDTO")),
        new OA\Response(response: 404, description: "Not found"),
        new OA\Response(response: 409, description: "Optimist lock"),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function update(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Update Relying party");
        $span = $this->startSpan("Update Relying party");
        $this->sql->begin();
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $value = $this->readRelyingParty($request);
            $result = $this->updateUsecase->update($uid, $value);
            $this->sql->commit();
            $value = $this->mapRelyingParty($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            $span->end();
        }
    }

    private function readRelyingParty(ServerRequestInterface $request): RelyingPartyUpdateParams
    {
        $this->logDebug("Read entity for Relying party");
        $span = $this->startSpan("Read entity for Relying party");
        try {
            $body = $request->getParsedBody();
            $errorsList = new ConstraintFailList();
            $value = new RelyingPartyUpdateParams();
            $value->uid(RelyingPartyUidVO::tryFrom($body['uid'] ?? null, $errorsList));
            $value->code(RelyingPartyCodeVO::tryFrom($body['code'] ?? null, $errorsList));
            $value->apiKey(RelyingPartyApiKeyVO::tryFrom($body['apiKey'] ?? null, $errorsList));
            $value->policies(RelyingPartyPoliciesVO::tryFrom($body['policies'] ?? null, $errorsList));
            $value->version(RelyingPartyVersionVO::tryFrom($body['version'] ?? null, $errorsList));
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $value;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function mapRelyingParty(RelyingPartyUpdateResult $value): RelyingPartyApiDTO
    {
        $this->logDebug("Map entity to output dto for Relying party");
        $span = $this->startSpan("Map entity to output dto for Relying party");
        try {
            $dto = new RelyingPartyApiDTO();
            $dto->uid = $value->getUid();
            $dto->code = $value->getCode();
            $dto->apiKey = $value->getApiKey();
            $dto->enabled = $value->isEnabled();
            $dto->scopes = $value->getScopes();
            $dto->schemas = $value->getSchemas();
            $dto->policies = $value->getPolicies();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
