<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\Role\Application\Usecase\Retrieve;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\Master\Role\Domain\RoleRef;
use Civi\Lughauth\Features\Access\Master\Role\Application\Service\Visibility\RoleVisibilityService;
use Civi\Lughauth\Features\Access\Master\Role\Domain\Gateway\RoleReadGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class RoleRetrieveUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly RoleVisibilityService $visibility,
        private readonly RoleReadGateway $reader
    ) {
    }

    public function allowRetrieve(?RoleRef $ref = null): Allow
    {
        $this->logDebug("Check allow of retrieve usecase for Role");
        $span = $this->startSpan("Check allow of retrieve usecase for Role");
        try {
            $result = $this->dispacher->dispatch(new RoleRetrieveAllowDecision(Allow::allowed('retrieve', 'Allowed to retrieve by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function hiddenFields(?RoleRef $ref = null): array
    {
        $this->logDebug("Check hidden fields usecase for Role");
        $span = $this->startSpan("Check hidden fields usecase for Role");
        try {
            return $this->visibility->fieldsToHide($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieve(string $uid): RoleRetrieveResult
    {
        $this->logDebug("Run retrieve usecase for Role");
        $span = $this->startSpan("Run retrieve usecase for Role");
        try {
            $ref = new RoleRef($uid);
            $allow = $this->allowRetrieve($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$result = $this->visibility->retrieveVisible($ref)) {
                throw new NotFoundException($uid);
            }
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new RoleRetrieveResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
