<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\Role\Application\Usecase\Update;

use Civi\Lughauth\Features\Access\Master\Role\Domain\ValueObject\Holder\RoleUidAttributeHolder;
use Civi\Lughauth\Features\Access\Master\Role\Domain\ValueObject\Holder\RoleNameAttributeHolder;
use Civi\Lughauth\Features\Access\Master\Role\Domain\ValueObject\Holder\RoleTenantAttributeHolder;
use Civi\Lughauth\Features\Access\Master\Role\Domain\ValueObject\Holder\RoleVersionAttributeHolder;
use Civi\Lughauth\Features\Access\Master\Role\Domain\RoleAttributes;

class RoleUpdateResult
{
    use RoleUidAttributeHolder;
    use RoleNameAttributeHolder;
    use RoleTenantAttributeHolder;
    use RoleVersionAttributeHolder;

    private const UNSETS = [
      'uid' => 'unsetUid',
      'name' => 'unsetName',
      'tenant' => 'unsetTenant',
      'version' => 'unsetVersion',
    ];

    public function __construct(RoleAttributes|null $att = null)
    {
        if ($att) {
            $this->uid($att->getUidOrDefault(null));
            $this->name($att->getNameOrDefault(null));
            $this->tenant($att->getTenantOrDefault(null));
            $this->version($att->getVersionOrDefault(null));
        }
    }
    public function toAttributes(): RoleAttributes
    {
        $att = new RoleAttributes();
        if ($this->uid) {
            $att->uid($this->uid);
        }
        if ($this->name) {
            $att->name($this->name);
        }
        if ($this->tenant) {
            $att->tenant($this->tenant);
        }
        if ($this->version) {
            $att->version($this->version);
        }
        return $att;
    }
    public function unset($field)
    {
        if (isset(self::UNSETS[$field])) {
            call_user_func([$this, self::UNSETS[$field]]);
        }
    }
    public function withDefaults(): RoleUpdateResult
    {
        $this->withDefaultUid();
        $this->withDefaultName();
        $this->withDefaultTenant();
        $this->withDefaultVersion();
        return $this;
    }
}
