<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\Role\Domain\Event;

use Override;
use Civi\Lughauth\Features\Access\Master\Role\Domain\Role;
use Civi\Lughauth\Shared\Event\PublicEvent;

class RoleUpdateEvent extends RoleEvent implements PublicEvent
{
    public function __construct(
        Role $payload,
        public readonly Role $original
    ) {
        parent::__construct($payload);
    }
    #[Override]
    public function eventType(): string
    {
        return 'role.update';
    }
    #[Override]
    public function schemaVersion(): string
    {
        return 'v1';
    }
    #[Override]
    public function payload(): array
    {
        return $this->payload->asPublicJson();
    }
    #[Override]
    public function original(): array
    {
        return $this->original->asPublicJson();
    }
}
