<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\Role\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Closure;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Master\Role\Domain\Role;
use Civi\Lughauth\Features\Access\Master\Role\Domain\RoleRef;
use Civi\Lughauth\Features\Access\Master\Role\Infrastructure\Driven\RoleWriteRepositoryAdapter;

class RoleWriteGateway
{
    private RoleWriteRepository $repository;
    public function __construct(ContainerInterface $container, ?RoleWriteRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(RoleWriteRepositoryAdapter::class);
    }
    public function create(Role $entity, ?Closure $verify = null): Role
    {
        return $this->repository->create($entity, $verify);
    }
    public function update(RoleRef $ref, Role $entity): Role
    {
        return $this->repository->update($ref, $entity);
    }
    public function delete(Role $entity): bool
    {
        return $this->repository->delete($entity);
    }
    public function listForUpdate(?RoleFilter $filter = null, ?RoleCursor $cursor = null): RoleSlide
    {
        return $this->repository->listForUpdate($filter, $cursor);
    }
    public function retrieveForUpdate(RoleFilter $filter): ?Role
    {
        return $this->repository->retrieveForUpdate($filter);
    }
    public function existsForUpdate(?RoleFilter $filter): bool
    {
        return $this->repository->existsForUpdate($filter);
    }
    public function countForUpdate(?RoleFilter $filter = null): int
    {
        return $this->repository->countForUpdate($filter);
    }
    public function findOneForUpdateByUid(string $uid): ?Role
    {
        return $this->repository->findOneForUpdateByUid($uid);
    }
    public function findOneForUpdateByTenantAndName(?TenantRef $tenant, string $name): ?Role
    {
        return $this->repository->findOneForUpdateByTenantAndName($tenant, $name);
    }
}
