<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\Role\Domain\ValueObject\Holder;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Master\Role\Domain\ValueObject\RoleTenantVO;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;

trait RoleTenantAttributeHolder
{
    protected RoleTenantVO|TenantRef|null $tenant = null;
    protected bool $tenantAssigned = false;

    public function getTenantOrDefault(?RoleTenantVO $tenant): ?RoleTenantVO
    {
        return $this->tenantAssigned ? ($this->tenant !== null ? RoleTenantVO::from($this->tenant) : null) : $tenant;
    }
    public function tenant(RoleTenantVO|TenantRef|null $tenant): static
    {
        $this->tenant = $tenant;
        $this->tenantAssigned = true;
        return $this;
    }
    public function getTenant(): ?TenantRef
    {
        return is_a($this->tenant, RoleTenantVO::class) ? $this->tenant->value() : $this->tenant;
    }
    public function unsetTenant(): static
    {
        $this->tenant = null;
        $this->tenantAssigned = false;
        return $this;
    }
    protected function withDefaultTenant(): void
    {
    }
    protected function withAssertedTenantRules(self $value, ConstraintFailList $errorsList): void
    {
        if ($value->tenantAssigned = $this->tenantAssigned) {
            $value->tenant = RoleTenantVO::tryFrom($this->tenant, $errorsList);
        }
    }
}
