<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\Role\Infrastructure\Driven;

use Psr\EventDispatcher\EventDispatcherInterface;
use Closure;
use Override;
use Throwable;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Master\Role\Domain\Gateway\RoleWriteRepository;
use Civi\Lughauth\Features\Access\Master\Role\Infrastructure\Connector\Pdo\RolePdoConnector;
use Civi\Lughauth\Features\Access\Master\Role\Domain\Gateway\RoleSlide;
use Civi\Lughauth\Features\Access\Master\Role\Domain\Gateway\RoleCursor;
use Civi\Lughauth\Features\Access\Master\Role\Domain\Gateway\RoleFilter;
use Civi\Lughauth\Features\Access\Master\Role\Domain\RoleRef;
use Civi\Lughauth\Features\Access\Master\Role\Domain\Role;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\EntityChangelog\EntityChangelogService;

class RoleWriteRepositoryAdapter implements RoleWriteRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly RolePdoConnector $conn,
        private readonly EventDispatcherInterface $dispacher,
        private readonly EntityChangelogService $changelog,
    ) {
    }
    #[Override]
    public function resolveForUpdate(RoleRef $ref): ?Role
    {
        return $this->conn->retrieveForUpdate(new RoleFilter(uids: [ $ref->uid() ]));
    }
    #[Override]
    public function listForUpdate(?RoleFilter $filter = null, ?RoleCursor $sort = null): RoleSlide
    {
        $this->logDebug("Count for Role on adapter ");
        $span = $this->startSpan("Count for Role on adapter");
        try {
            $values = $this->conn->listForUpdate($filter, $sort);
            $last = end($values);
            return new RoleSlide(function ($slide, $next) use ($filter) {
                return $this->listForUpdate($filter, $next);
            }, new RoleCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieveForUpdate(RoleFilter $filter): ?Role
    {
        $this->logDebug("Count for Role on adapter ");
        $span = $this->startSpan("Count for Role on adapter");
        try {
            return $this->conn->retrieveForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function existsForUpdate(?RoleFilter $filter): bool
    {
        $this->logDebug("Count for Role on adapter ");
        $span = $this->startSpan("Count for Role on adapter");
        try {
            return $this->conn->existsForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function countForUpdate(?RoleFilter $filter = null): int
    {
        $this->logDebug("Count for Role on adapter ");
        $span = $this->startSpan("Count for Role on adapter");
        try {
            return $this->conn->countForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function create(Role $entity, ?Closure $verify = null): Role
    {
        $this->logDebug("Count for Role on adapter ");
        $span = $this->startSpan("Count for Role on adapter");
        try {
            $created = $this->conn->create($entity, $verify);
            $this->dispach($entity);
            $this->changelog->recordChange('role', $entity->uid(), $entity->asPublicJson(), []);
            return $created;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function update(RoleRef $reference, Role $entity): Role
    {
        $this->logDebug("Count for Role on adapter ");
        $span = $this->startSpan("Count for Role on adapter");
        try {
            $updated = $this->conn->update($entity);
            $this->dispach($entity);
            $original = ($reference instanceof Role) ? $reference : $this->conn->retrieve(new RoleFilter(uids: [ $reference->uid() ]));
            $this->changelog->recordChange('role', $entity->uid(), $entity->asPublicJson(), $original->asPublicJson());
            return $updated;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function delete(Role $entity): bool
    {
        $this->logDebug("Count for Role on adapter ");
        $span = $this->startSpan("Count for Role on adapter");
        try {
            $result = $this->conn->delete($entity);
            $this->dispach($entity);
            $this->changelog->recordDeletion('role', $entity->uid(), $entity->asPublicJson());
            return $result;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByUid(string $uid): ?Role
    {
        $this->logDebug("Find on by uid for Role on adapter");
        $span = $this->startSpan("Find on by uid for Role on adapter");
        try {
            return $this->conn->retrieveForUpdate(new RoleFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByTenantAndName(?TenantRef $tenant, string $name): ?Role
    {
        $this->logDebug("Find on by tenant name for Role on adapter");
        $span = $this->startSpan("Find on by tenant name for Role on adapter");
        try {
            return $this->conn->retrieveForUpdate(new RoleFilter(tenantAndName: RoleFilter::tenantAndNameFilter(tenant: $tenant, name: $name)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function dispach(Role $entity)
    {
        $this->logDebug("Count for Role on adapter ");
        $span = $this->startSpan("Count for Role on adapter");
        try {
            foreach ($entity->getTheEvents() as $event) {
                $this->dispacher->dispatch($event);
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
