<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\Role\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\Master\Role\Application\Usecase\Create\RoleCreateUsecase;
use Civi\Lughauth\Features\Access\Master\Role\Domain\ValueObject\RoleUidVO;
use Civi\Lughauth\Features\Access\Master\Role\Domain\ValueObject\RoleNameVO;
use Civi\Lughauth\Features\Access\Master\Role\Domain\ValueObject\RoleTenantVO;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Master\Role\Domain\ValueObject\RoleVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Master\Role\Application\Usecase\Create\RoleCreateParams;
use Civi\Lughauth\Features\Access\Master\Role\Application\Usecase\Create\RoleCreateResult;

class RoleCreateController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SqlTemplate $sql,
        private readonly RoleCreateUsecase $createUsecase,
    ) {
    }
    #[OA\Post(
        path: "/api/access/roles",
        tags: ["Role"],
        description:"Create",
        requestBody: new OA\RequestBody(content: new OA\JsonContent(type: "object", ref:"#/components/schemas/RoleApiDTO")),
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/RoleApiDTO")),
              new OA\Response(response: 422, description: "Constraint error"),
          ]
    )]
    public function create(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Create Role");
        $span = $this->startSpan("Create Role");
        $this->sql->begin();
        try {
            $value = $this->readRole($request);
            $result = $this->createUsecase->create($value);
            $this->sql->commit();
            $value = $this->mapRole($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(201)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            $span->end();
        }
    }

    private function readRole(ServerRequestInterface $request): RoleCreateParams
    {
        $this->logDebug("Read entity for Role");
        $span = $this->startSpan("Read entity for Role");
        try {
            $body = $request->getParsedBody();
            $errorsList = new ConstraintFailList();
            $value = new RoleCreateParams();
            $value->uid(RoleUidVO::tryFrom($body['uid'] ?? null, $errorsList));
            $value->name(RoleNameVO::tryFrom($body['name'] ?? null, $errorsList));
            if (in_array('tenant', array_keys($body))) {
                $value->tenant(RoleTenantVO::tryFrom(isset($body['tenant']['$ref']) ? new TenantRef($body['tenant']['$ref']) : null, $errorsList));
            }
            $value->version(RoleVersionVO::tryFrom($body['version'] ?? null, $errorsList));
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $value;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function mapRole(RoleCreateResult $value): RoleApiDTO
    {
        $this->logDebug("Map entity to output dto for Role");
        $span = $this->startSpan("Map entity to output dto for Role");
        try {
            $tenant = $value->getTenant();
            $dto = new RoleApiDTO();
            $dto->uid = $value->getUid();
            $dto->name = $value->getName();
            $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
