<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\Tenant\Application\Usecase\Update;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Master\Tenant\Application\Service\Visibility\TenantVisibilityService;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\Gateway\TenantWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantUpdateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantVisibilityService $visibility,
        private readonly TenantWriteGateway $writer
    ) {
    }

    public function allowUpdate(?TenantRef $ref = null): Allow
    {
        $this->logDebug("Check allow update usecase for Tenant");
        $span = $this->startSpan("Check allow update usecase for Tenant");
        try {
            $result = $this->dispacher->dispatch(new TenantUpdateAllowDecision(Allow::allowed('update', 'Allowed to update by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fixedFields(?TenantRef $ref = null): array
    {
        $this->logDebug("Check fixed fields for update usecase for Tenant");
        $span = $this->startSpan("Check fixed fields for update usecase for Tenant");
        try {
            return $this->visibility->fieldsToFix($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function update(string $uid, TenantUpdateParams $params): TenantUpdateResult
    {
        $this->logDebug("Run update usecase for Tenant");
        $span = $this->startSpan("Run update usecase for Tenant");
        try {
            $ref = new TenantRef($uid);
            $allow = $this->allowUpdate($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new TenantUpdateCheck($params, $original));
            $enriched = $this->dispacher->dispatch(new TenantUpdateEnrich($params, $original, $params->toAttributes()));
            $attributes = $enriched->getResult();
            $input = $this->visibility->copyWithFixed($attributes);
            $modified = $original->update($input);
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new TenantUpdateResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
