<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\Tenant\Domain\Event;

use Override;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\Tenant;
use Civi\Lughauth\Shared\Event\PublicEvent;

class TenantUpdateEvent extends TenantEvent implements PublicEvent
{
    public function __construct(
        Tenant $payload,
        public readonly Tenant $original
    ) {
        parent::__construct($payload);
    }
    #[Override]
    public function eventType(): string
    {
        return 'tenant.update';
    }
    #[Override]
    public function schemaVersion(): string
    {
        return 'v1';
    }
    #[Override]
    public function payload(): array
    {
        return $this->payload->asPublicJson();
    }
    #[Override]
    public function original(): array
    {
        return $this->original->asPublicJson();
    }
}
