<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\Tenant\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\Tenant;
use Civi\Lughauth\Features\Access\Master\Tenant\Infrastructure\Driven\TenantReadRepositoryAdapter;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;

class TenantReadGateway
{
    private TenantReadRepository $repository;
    public function __construct(ContainerInterface $container, ?TenantReadRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(TenantReadRepositoryAdapter::class);
    }
    public function resolve(TenantRef $ref): ?Tenant
    {
        return $this->repository->resolve($ref);
    }
    public function list(?TenantFilter $filter = null, ?TenantCursor $cursor = null): TenantSlide
    {
        return $this->repository->list($filter, $cursor);
    }
    public function retrieve(TenantFilter $filter): ?Tenant
    {
        return $this->repository->retrieve($filter);
    }
    public function exists(?TenantFilter $filter): bool
    {
        return $this->repository->exists($filter);
    }
    public function count(?TenantFilter $filter = null): int
    {
        return $this->repository->count($filter);
    }
    public function findOneByUid(string $uid): ?Tenant
    {
        return $this->repository->findOneByUid($uid);
    }
    public function findOneByName(string $name): ?Tenant
    {
        return $this->repository->findOneByName($name);
    }
    public function findOneByDomain(string $domain): ?Tenant
    {
        return $this->repository->findOneByDomain($domain);
    }
}
