<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\Tenant\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Closure;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\Tenant;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Master\Tenant\Infrastructure\Driven\TenantWriteRepositoryAdapter;

class TenantWriteGateway
{
    private TenantWriteRepository $repository;
    public function __construct(ContainerInterface $container, ?TenantWriteRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(TenantWriteRepositoryAdapter::class);
    }
    public function create(Tenant $entity, ?Closure $verify = null): Tenant
    {
        return $this->repository->create($entity, $verify);
    }
    public function update(TenantRef $ref, Tenant $entity): Tenant
    {
        return $this->repository->update($ref, $entity);
    }
    public function delete(Tenant $entity): bool
    {
        return $this->repository->delete($entity);
    }
    public function listForUpdate(?TenantFilter $filter = null, ?TenantCursor $cursor = null): TenantSlide
    {
        return $this->repository->listForUpdate($filter, $cursor);
    }
    public function retrieveForUpdate(TenantFilter $filter): ?Tenant
    {
        return $this->repository->retrieveForUpdate($filter);
    }
    public function existsForUpdate(?TenantFilter $filter): bool
    {
        return $this->repository->existsForUpdate($filter);
    }
    public function countForUpdate(?TenantFilter $filter = null): int
    {
        return $this->repository->countForUpdate($filter);
    }
    public function findOneForUpdateByUid(string $uid): ?Tenant
    {
        return $this->repository->findOneForUpdateByUid($uid);
    }
    public function findOneForUpdateByName(string $name): ?Tenant
    {
        return $this->repository->findOneForUpdateByName($name);
    }
    public function findOneForUpdateByDomain(string $domain): ?Tenant
    {
        return $this->repository->findOneForUpdateByDomain($domain);
    }
}
