<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\Tenant\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\Tenant\Domain\ValueObject\TenantNameVO;

trait TenantNameAccesor
{
    private TenantNameVO $_name;
    public function getName(): string
    {
        return $this->_name->value();
    }
    public function name(): TenantNameVO
    {
        return $this->_name;
    }
    public function isNameChanged(?self $original = null): bool
    {
        return $this->_name->value() !== ($original ? $original->_name->value() : null);
    }
    public function withName(TenantNameVO|string $name): self
    {
        $copy = clone $this;
        $copy->_name = TenantNameVO::from($name);
        return $copy;
    }
}
