namespace Civi\Lughauth\Features\Access\Master\Tenant\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\Tenant\Domain\ValueObject\TenantVersionVO;

trait TenantVersionAccesor
{
    private ?TenantVersionVO $_version = null;
    public function getVersion(): ?int
    {
        return $this->_version?->value();
    }
    public function version(): ?TenantVersionVO
    {
        return $this->_version;
    }
    public function isVersionChanged(?self $original = null): bool
    {
        return $this->_version->value() !== ($original ? $original->_version->value() : null);
    }
    public function withVersion(TenantVersionVO|int|null $version): self
    {
        $copy = clone $this;
        $copy->_version = TenantVersionVO::from($version);
        return $copy;
    }
}
