<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Service\Visibility;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Features\Access\Master\Tenant\Application\Service\Visibility\TenantVisibilityService;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\TenantConfigRef;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\TenantConfigAttributes;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\TenantConfig;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\Gateway\TenantConfigFilter;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\Gateway\TenantConfigCursor;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\Gateway\TenantConfigReadGateway;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\Gateway\TenantConfigSlide;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\Gateway\TenantConfigWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantConfigVisibilityService
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantConfigReadGateway $readGateway,
        private readonly TenantConfigWriteGateway $writeGateway,
        private readonly TenantVisibilityService $tenantVisibilityService
    ) {
    }

    public function prepareVisibleDataInList(TenantConfig $content): TenantConfigAttributes
    {
        return $this->prepareVisibleDataCallback($content, true);
    }
    public function prepareVisibleData(TenantConfig $content): TenantConfigAttributes
    {
        return $this->prepareVisibleDataCallback($content, false);
    }
    public function copyWithFixed(TenantConfigAttributes $attributes, ?TenantConfig $original = null): TenantConfigAttributes
    {
        $this->logDebug("Copy with fixed for Tenant config");
        $span = $this->startSpan("Copy with fixed for  Tenant config");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new TenantConfigPresetValues($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(TenantConfigAttributes $attributes, ?TenantConfig $original = null): TenantConfigAttributes
    {
        $this->logDebug("Copy with hidden for Tenant config");
        $span = $this->startSpan("Copy with hidden for  Tenant config");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|TenantConfigRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for Tenant config");
        $span = $this->startSpan("Check visibility of an iterator for  Tenant config");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new TenantConfigFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, TenantConfigRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new TenantConfigRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(TenantConfigFilter $filter, ?TenantConfigCursor $cursor = null): TenantConfigSlide
    {
        $this->logDebug("List visibles for Tenant config");
        $span = $this->startSpan("List visibles for  Tenant config");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisibles(TenantConfigFilter $filter): int
    {
        $this->logDebug("Count visibles for Tenant config");
        $span = $this->startSpan("Count visibles for  Tenant config");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->readGateway->count($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisiblesForUpdate(TenantConfigFilter $filter, ?TenantConfigCursor $cursor = null): TenantConfigSlide
    {
        $this->logDebug("List visibles for update for Tenant config");
        $span = $this->startSpan("List visibles for update for  Tenant config");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->writeGateway->listForUpdate($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisiblesForUpdate(TenantConfigFilter $filter): int
    {
        $this->logDebug("Count visibles for update for Tenant config");
        $span = $this->startSpan("Count visibles for update for  Tenant config");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(TenantConfigRef $ref): ?TenantConfig
    {
        $this->logDebug("Retrieve visible for Tenant config");
        $span = $this->startSpan("Retrieve visible for  Tenant config");
        try {
            $filter = new TenantConfigFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(TenantConfigRef $ref): ?TenantConfig
    {
        $this->logDebug("Retrieve visible for update for Tenant config");
        $span = $this->startSpan("Retrieve visible for update for  Tenant config");
        try {
            $filter = new TenantConfigFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?TenantConfigRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for Tenant config");
        $span = $this->startSpan("Check fields for fix for Tenant config");
        try {
            $result = $this->dispacher->dispatch(new TenantConfigCollectNonEditableFields(TenantConfig::calculatedFields(), $ref));
            return array_values(array_unique(array_merge($this->fieldsToHide($ref), $result->fields)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?TenantConfigRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for Tenant config");
        $span = $this->startSpan("Check fields to hide for  Tenant config");
        try {
            $result = $this->dispacher->dispatch(new TenantConfigCollectNonVisibleFields([], $ref));
            return array_values(array_unique($result->fields));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(TenantConfigAttributes $attributes): TenantConfigAttributes
    {
        $this->logDebug("Check visibility of parent references for Tenant config");
        $span = $this->startSpan("Check visibility of parent references for  Tenant config");
        try {
            if ($attributes->getTenant() && !$this->tenantVisibilityService->checkVisibility($attributes->getTenant())) {
                throw new NotFoundException("Unknow Tenant " . $attributes->getTenant());
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(TenantConfigFilter $filter)
    {
        $this->logDebug("Compose visibility filter for Tenant config");
        $span = $this->startSpan("Compose visibility filter for  Tenant config");
        try {
            $result = $this->dispacher->dispatch(new TenantConfigRestrictFilterToVisibility($filter));
            return $result->tenantConfigFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(TenantConfig $value): bool
    {
        $this->logDebug("Check if item is visible for Tenant config");
        $span = $this->startSpan("Check if item is visible for  Tenant config");
        try {
            $result = $this->dispacher->dispatch(new TenantConfigVisibilityCheck(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function prepareVisibleDataCallback(TenantConfig $content, bool $inlist): TenantConfigAttributes
    {
        $this->logDebug("Prepare hidratation to visible data for Tenant config");
        $span = $this->startSpan("Prepare hidratation to visible data for Tenant config");
        try {
            $attributes = $content->toAttributes();
            $result = $this->dispacher->dispatch(new TenantConfigEnrichForView($content, $inlist, $attributes));
            return $result->getAttributes();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
