<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\Gateway;

use Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\TenantConfig;

class TenantConfigCursor
{
    public function __construct(
        private int $limit,
        private ?string $sinceUid = null,
    ) {
    }
    public function next(TenantConfig $tenantConfig, ?int $size): TenantConfigCursor
    {
        $next = clone $this;
        $next->sinceUid = $tenantConfig->uid();
        $next->limit = $size ?? $this->limit;
        return $next;
    }
    public function limit(): int
    {
        return $this->limit;
    }
    public function sinceUid(): ?string
    {
        return $this->sinceUid;
    }
}
