<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\TenantConfig;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Infrastructure\Driven\TenantConfigReadRepositoryAdapter;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\TenantConfigRef;

class TenantConfigReadGateway
{
    private TenantConfigReadRepository $repository;
    public function __construct(ContainerInterface $container, ?TenantConfigReadRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(TenantConfigReadRepositoryAdapter::class);
    }
    public function resolve(TenantConfigRef $ref): ?TenantConfig
    {
        return $this->repository->resolve($ref);
    }
    public function list(?TenantConfigFilter $filter = null, ?TenantConfigCursor $cursor = null): TenantConfigSlide
    {
        return $this->repository->list($filter, $cursor);
    }
    public function retrieve(TenantConfigFilter $filter): ?TenantConfig
    {
        return $this->repository->retrieve($filter);
    }
    public function exists(?TenantConfigFilter $filter): bool
    {
        return $this->repository->exists($filter);
    }
    public function count(?TenantConfigFilter $filter = null): int
    {
        return $this->repository->count($filter);
    }
    public function findOneByUid(string $uid): ?TenantConfig
    {
        return $this->repository->findOneByUid($uid);
    }
    public function findOneByTenant(TenantRef $tenant): ?TenantConfig
    {
        return $this->repository->findOneByTenant($tenant);
    }
}
