<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\TenantConfig\Domain\ValueObject\TenantConfigTenantVO;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;

trait TenantConfigTenantAccesor
{
    private TenantConfigTenantVO $_tenant;
    public function getTenant(): TenantRef
    {
        return $this->_tenant->value();
    }
    public function tenant(): TenantConfigTenantVO
    {
        return $this->_tenant;
    }
    public function isTenantChanged(?self $original = null): bool
    {
        return $this->_tenant->value() !== ($original ? $original->_tenant->value() : null);
    }
    public function withTenant(TenantConfigTenantVO|TenantRef $tenant): self
    {
        $copy = clone $this;
        $copy->_tenant = TenantConfigTenantVO::from($tenant);
        return $copy;
    }
}
