<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantConfig\Infrastructure\Driver;

use Psr\Container\ContainerInterface;
use Override;
use Slim\Routing\RouteCollectorProxy;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Infrastructure\Driver\Rest\TenantConfigAllowController;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Infrastructure\Driver\Rest\TenantConfigListController;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Infrastructure\Driver\Rest\TenantConfigCreateController;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Infrastructure\Driver\Rest\TenantConfigRetrieveController;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Infrastructure\Driver\Rest\TenantConfigUpdateController;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Infrastructure\Driver\Rest\TenantConfigDeleteController;
use Civi\Lughauth\Shared\Security\Rbac\Handler;
use Civi\Lughauth\Shared\Infrastructure\MicroPlugin;
use Civi\Lughauth\Shared\Infrastructure\MicroPlugin\GenericSecurityPlugin;
use Civi\Lughauth\Shared\Infrastructure\StartupProcessor;
use Civi\Lughauth\Shared\Event\EventListenersRegistrarInterface;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Service\Visibility\TenantConfigRestrictFilterToVisibility;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Policy\Filter\TenantAccesible;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Policy\Fields\FixTenantExcludingRoot;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Service\Visibility\TenantConfigCollectNonEditableFields;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Policy\Allow\Create\IsAutenticatedCreateAllow;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Usecase\Create\TenantConfigCreateAllowDecision;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Policy\Allow\Update\IsAutenticatedUpdateAllow;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Usecase\Update\TenantConfigUpdateAllowDecision;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Policy\Allow\Retrieve\IsAutenticatedRetrieveAllow;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Usecase\Retrieve\TenantConfigRetrieveAllowDecision;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Policy\Allow\List\IsAutenticatedListAllow;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Usecase\List\TenantConfigListAllowDecision;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Policy\Allow\Delete\IsAutenticatedDeleteAllow;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Application\Usecase\Delete\TenantConfigDeleteAllowDecision;
use Civi\Lughauth\Features\Access\Master\TenantConfig\Infrastructure\Driven\TenantConfigChangelogSync;
use Civi\Lughauth\Shared\Infrastructure\EntityChangeLog\EntityChangeLogSyncEvent;

class TenantConfigPlugin extends MicroPlugin
{
    #[Override]
    public function registerRoutes(RouteCollectorProxy $app)
    {
        $app->group('/api/access/tenants-config', [$this, 'setRoutesForTenantConfig']);
        $app->group('/api/me/acl/access/tenants-config', [$this, 'setRoutesForTenantConfigAcl']);
    }
    #[Override]
    public function registerEvents(EventListenersRegistrarInterface $bus)
    {
        $bus->registerListener(TenantConfigRestrictFilterToVisibility::class, TenantAccesible::class);
        $bus->registerListener(TenantConfigCollectNonEditableFields::class, FixTenantExcludingRoot::class);
        $bus->registerListener(TenantConfigCreateAllowDecision::class, IsAutenticatedCreateAllow::class);
        $bus->registerListener(TenantConfigUpdateAllowDecision::class, IsAutenticatedUpdateAllow::class);
        $bus->registerListener(TenantConfigRetrieveAllowDecision::class, IsAutenticatedRetrieveAllow::class);
        $bus->registerListener(TenantConfigListAllowDecision::class, IsAutenticatedListAllow::class);
        $bus->registerListener(TenantConfigDeleteAllowDecision::class, IsAutenticatedDeleteAllow::class);
        $bus->registerListener(EntityChangeLogSyncEvent::class, TenantConfigChangelogSync::class);
    }
    #[Override]
    public function registerStartup(StartupProcessor $processor): void
    {
        $processor->register(function (ContainerInterface $container) {
            $handler = $container->get(Handler::class);
            $handler->registerResourceAction("tenant-config", "list", "READ");
            $handler->registerResourceAction("tenant-config", "create", "WRITE");
            $handler->registerResourceAction("tenant-config", "retrieve", "READ");
            $handler->registerResourceAction("tenant-config", "update", "WRITE");
            $handler->registerResourceAction("tenant-config", "delete", "WRITE");

            $handler->registerResourceAttribute("tenant-config", "tenant", "MANAGE");
            $handler->registerResourceAttribute("tenant-config", "innerLabel", "MANAGE");
            $handler->registerResourceAttribute("tenant-config", "forceMfa", "MANAGE");
            $handler->registerResourceAttribute("tenant-config", "allowRegister", "MANAGE");
            $handler->registerResourceAttribute("tenant-config", "enableRegisterUsers", "MANAGE");
            $handler->registerResourceAttribute("tenant-config", "wellcomeEmail", "MANAGE");
            $handler->registerResourceAttribute("tenant-config", "registerdEmail", "MANAGE");
            $handler->registerResourceAttribute("tenant-config", "disabledUserEmail", "MANAGE");
            $handler->registerResourceAttribute("tenant-config", "enabledUserEmail", "MANAGE");
            $handler->registerResourceAttribute("tenant-config", "allowRecoverPass", "MANAGE");
            $handler->registerResourceAttribute("tenant-config", "recoverPassEmail", "MANAGE");
            $handler->registerResourceAttribute("tenant-config", "version", "MANAGE");
        }, StartupProcessor::before(GenericSecurityPlugin::STARTUP_ORDER));
    }
    public function setRoutesForTenantConfigAcl(RouteCollectorProxy $tenantConfigGroup)
    {
        $tenantConfigGroup->get('', [TenantConfigAllowController::class, 'genericAllow']);
        $tenantConfigGroup->get('/{uid}', [TenantConfigAllowController::class, 'contextualAllow']);
    }
    public function setRoutesForTenantConfig(RouteCollectorProxy $tenantConfigGroup)
    {
        $tenantConfigGroup->get('', [TenantConfigListController::class, 'list']);
        $tenantConfigGroup->post('', [TenantConfigCreateController::class, 'create']);
        $tenantConfigGroup->get('/{uid}', [TenantConfigRetrieveController::class, 'retrieve']);
        $tenantConfigGroup->put('/{uid}', [TenantConfigUpdateController::class, 'update']);
        $tenantConfigGroup->delete('/{uid}', [TenantConfigDeleteController::class, 'delete']);
        $tenantConfigGroup->delete('', [TenantConfigDeleteController::class, 'deleteAllForQuery']);
        $tenantConfigGroup->get('/~/delete-status/{uid}', [TenantConfigDeleteController::class, 'checkDeleteAllForQueryStatus']);
    }
}
