<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantLoginProvider\Application\Usecase\Delete;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\Master\TenantLoginProvider\Domain\TenantLoginProviderRef;
use Civi\Lughauth\Features\Access\Master\TenantLoginProvider\Application\Service\Visibility\TenantLoginProviderVisibilityService;
use Civi\Lughauth\Features\Access\Master\TenantLoginProvider\Domain\Gateway\TenantLoginProviderWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantLoginProviderDeleteUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantLoginProviderVisibilityService $visibility,
        private readonly TenantLoginProviderWriteGateway $writer
    ) {
    }

    public function allowDelete(?TenantLoginProviderRef $ref = null): Allow
    {
        $this->logDebug("Check allow of delete usecase for Tenant login provider");
        $span = $this->startSpan("Check allow of delete usecase for Tenant login provider");
        try {
            $result = $this->dispacher->dispatch(new TenantLoginProviderDeleteAllowDecision(Allow::allowed('delete', 'Allowed to delete by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function delete(string $uid): void
    {
        $this->logDebug("Run delete usecase for Tenant login provider");
        $span = $this->startSpan("Run delete usecase for Tenant login provider");
        try {
            $ref = new TenantLoginProviderRef($uid);
            $allow = $this->allowDelete($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new TenantLoginProviderDeleteCheck($original));
            $deleted = $original->delete();
            $this->writer->delete($deleted);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
