trait TenantLoginProviderCertificateAccesor
{
    private ?TenantLoginProviderCertificateVO $_certificate = null;
    public function getCertificate(): ?string
    {
        return $this->_certificate?->value();
    }
    public function certificate(): ?TenantLoginProviderCertificateVO
    {
        return $this->_certificate;
    }
    public function isCertificateChanged(?self $original = null): bool
    {
        return $this->_certificate->value() !== ($original ? $original->_certificate->value() : null);
    }
    public function withCertificate(TenantLoginProviderCertificateVO|string|null $certificate): self
    {
        $copy = clone $this;
        $copy->_certificate = TenantLoginProviderCertificateVO::from($certificate);
        return $copy;
    }
}
