<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantLoginProvider\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\TenantLoginProvider\Domain\ValueObject\TenantLoginProviderTenantVO;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;

trait TenantLoginProviderTenantAccesor
{
    private TenantLoginProviderTenantVO $_tenant;
    public function getTenant(): TenantRef
    {
        return $this->_tenant->value();
    }
    public function tenant(): TenantLoginProviderTenantVO
    {
        return $this->_tenant;
    }
    public function isTenantChanged(?self $original = null): bool
    {
        return $this->_tenant->value() !== ($original ? $original->_tenant->value() : null);
    }
    public function withTenant(TenantLoginProviderTenantVO|TenantRef $tenant): self
    {
        $copy = clone $this;
        $copy->_tenant = TenantLoginProviderTenantVO::from($tenant);
        return $copy;
    }
}
