    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantPartySecurityReadGateway $readGateway,
        private readonly TenantPartySecurityWriteGateway $writeGateway,
        private readonly TenantVisibilityService $tenantVisibilityService,
        private readonly RelyingPartyVisibilityService $relyingPartyVisibilityService
    ) {
    }

    public function prepareVisibleDataInList(TenantPartySecurity $content): TenantPartySecurityAttributes
    {
        return $this->prepareVisibleDataCallback($content, true);
    }
    public function prepareVisibleData(TenantPartySecurity $content): TenantPartySecurityAttributes
    {
        return $this->prepareVisibleDataCallback($content, false);
    }
    public function copyWithFixed(TenantPartySecurityAttributes $attributes, ?TenantPartySecurity $original = null): TenantPartySecurityAttributes
    {
        $this->logDebug("Copy with fixed for Tenant party security");
        $span = $this->startSpan("Copy with fixed for  Tenant party security");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new TenantPartySecurityPresetValues($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(TenantPartySecurityAttributes $attributes, ?TenantPartySecurity $original = null): TenantPartySecurityAttributes
    {
        $this->logDebug("Copy with hidden for Tenant party security");
        $span = $this->startSpan("Copy with hidden for  Tenant party security");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|TenantPartySecurityRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for Tenant party security");
        $span = $this->startSpan("Check visibility of an iterator for  Tenant party security");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new TenantPartySecurityFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, TenantPartySecurityRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new TenantPartySecurityRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(TenantPartySecurityFilter $filter, ?TenantPartySecurityCursor $cursor = null): TenantPartySecuritySlide
    {
        $this->logDebug("List visibles for Tenant party security");
        $span = $this->startSpan("List visibles for  Tenant party security");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
