    {
        $this->logDebug("Count visibles for update for Tenant party security");
        $span = $this->startSpan("Count visibles for update for  Tenant party security");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(TenantPartySecurityRef $ref): ?TenantPartySecurity
    {
        $this->logDebug("Retrieve visible for Tenant party security");
        $span = $this->startSpan("Retrieve visible for  Tenant party security");
        try {
            $filter = new TenantPartySecurityFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(TenantPartySecurityRef $ref): ?TenantPartySecurity
    {
        $this->logDebug("Retrieve visible for update for Tenant party security");
        $span = $this->startSpan("Retrieve visible for update for  Tenant party security");
        try {
            $filter = new TenantPartySecurityFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?TenantPartySecurityRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for Tenant party security");
        $span = $this->startSpan("Check fields for fix for Tenant party security");
        try {
            $result = $this->dispacher->dispatch(new TenantPartySecurityCollectNonEditableFields(TenantPartySecurity::calculatedFields(), $ref));
            return array_values(array_unique(array_merge($this->fieldsToHide($ref), $result->fields)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?TenantPartySecurityRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for Tenant party security");
        $span = $this->startSpan("Check fields to hide for  Tenant party security");
        try {
            $result = $this->dispacher->dispatch(new TenantPartySecurityCollectNonVisibleFields([], $ref));
            return array_values(array_unique($result->fields));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(TenantPartySecurityAttributes $attributes): TenantPartySecurityAttributes
    {
        $this->logDebug("Check visibility of parent references for Tenant party security");
        $span = $this->startSpan("Check visibility of parent references for  Tenant party security");
        try {
            if ($attributes->getTenant() && !$this->tenantVisibilityService->checkVisibility($attributes->getTenant())) {
                throw new NotFoundException("Unknow Tenant " . $attributes->getTenant());
            }
            if ($attributes->getRelyingParty() && !$this->relyingPartyVisibilityService->checkVisibility($attributes->getRelyingParty())) {
                throw new NotFoundException("Unknow RelyingParty " . $attributes->getRelyingParty());
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(TenantPartySecurityFilter $filter)
    {
        $this->logDebug("Compose visibility filter for Tenant party security");
        $span = $this->startSpan("Compose visibility filter for  Tenant party security");
        try {
            $result = $this->dispacher->dispatch(new TenantPartySecurityRestrictFilterToVisibility($filter));
            return $result->tenantPartySecurityFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(TenantPartySecurity $value): bool
    {
        $this->logDebug("Check if item is visible for Tenant party security");
        $span = $this->startSpan("Check if item is visible for  Tenant party security");
        try {
            $result = $this->dispacher->dispatch(new TenantPartySecurityVisibilityCheck(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
