        $this->logDebug("Prepare hidratation to visible data for Tenant party security");
        $span = $this->startSpan("Prepare hidratation to visible data for Tenant party security");
        try {
            $attributes = $content->toAttributes();
            $result = $this->dispacher->dispatch(new TenantPartySecurityEnrichForView($content, $inlist, $attributes));
            return $result->getAttributes();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
