<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Application\Usecase\List;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Application\Service\Visibility\TenantPartySecurityVisibilityService;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\Gateway\TenantPartySecurityReadGateway;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\Gateway\TenantPartySecurityFilter;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\Gateway\TenantPartySecurityCursor;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\Gateway\TenantPartySecurityAttributesSlide;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantPartySecurityListUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantPartySecurityVisibilityService $visibility,
        private readonly TenantPartySecurityReadGateway $reader
    ) {
    }

    public function allowList(): Allow
    {
        $this->logDebug("Check allow of list usecase for Tenant party security");
        $span = $this->startSpan("Check allow of list usecase for Tenant party security");
        try {
            $result = $this->dispacher->dispatch(new TenantPartySecurityListAllowDecision(Allow::allowed('list', 'Allowed to list by default')));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function count(TenantPartySecurityFilter $filter): int
    {
        $this->logDebug("Run list usecase for Tenant party security");
        $span = $this->startSpan("Run list usecase for Tenant party security");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            return $this->visibility->countVisibles($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function list(TenantPartySecurityFilter $filter, ?TenantPartySecurityCursor $cursor = null): TenantPartySecurityAttributesSlide
    {
        $this->logDebug("Run list usecase for Tenant party security");
        $span = $this->startSpan("Run list usecase for Tenant party security");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $slide = $this->visibility->listVisibles($filter, $cursor);
            return new TenantPartySecurityAttributesSlide($slide->nextCursor(), array_map(fn ($item) => $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($item)), $slide->values()));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
