<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain;

use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\Holder\TenantPartySecurityUidAttributeHolder;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\TenantPartySecurityUidVO;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\Holder\TenantPartySecurityTenantAttributeHolder;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\TenantPartySecurityTenantVO;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\Holder\TenantPartySecurityRelyingPartyAttributeHolder;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\TenantPartySecurityRelyingPartyVO;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\Holder\TenantPartySecurityPoliciesAttributeHolder;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\TenantPartySecurityPoliciesVO;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\Holder\TenantPartySecurityVersionAttributeHolder;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\TenantPartySecurityVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;

class TenantPartySecurityAttributes
{
    use TenantPartySecurityUidAttributeHolder;
    use TenantPartySecurityTenantAttributeHolder;
    use TenantPartySecurityRelyingPartyAttributeHolder;
    use TenantPartySecurityPoliciesAttributeHolder;
    use TenantPartySecurityVersionAttributeHolder;

    private const UNSETS = [
      'uid' => 'unsetUid',
      'tenant' => 'unsetTenant',
      'relyingParty' => 'unsetRelyingParty',
      'policies' => 'unsetPolicies',
      'version' => 'unsetVersion',
    ];

    public function build(): TenantPartySecurity
    {
        $errors = new ConstraintFailList();
        $uid = TenantPartySecurityUidVO::tryFrom($this->uid, $errors);
        $tenant = TenantPartySecurityTenantVO::tryFrom($this->tenant, $errors);
        $relyingParty = TenantPartySecurityRelyingPartyVO::tryFrom($this->relyingParty, $errors);
        $policies = TenantPartySecurityPoliciesVO::tryFrom($this->policies, $errors);
        $version = TenantPartySecurityVersionVO::tryFrom($this->version, $errors);
        if ($errors->hasErrors()) {
            throw $errors->asConstraintException();
        }    return new TenantPartySecurity(
            uid: $uid,
            tenant: $tenant,
            relyingParty: $relyingParty,
            policies: $policies,
            version: $version,
        );
    }
    public function withAssertedRules(): TenantPartySecurityAttributes
    {
        $value = new TenantPartySecurityAttributes();
        $errorsList = new ConstraintFailList();
        $this->withAssertedUidRules($value, $errorsList);
        $this->withAssertedTenantRules($value, $errorsList);
        $this->withAssertedRelyingPartyRules($value, $errorsList);
        $this->withAssertedPoliciesRules($value, $errorsList);
        $this->withAssertedVersionRules($value, $errorsList);
        if ($errorsList->hasErrors()) {
            throw $errorsList->asConstraintException();
        }
        return $value;
    }
    public function unset($field)
    {
        if (isset(self::UNSETS[$field])) {
            call_user_func([$this, self::UNSETS[$field]]);
        }
    }
    public function withDefaults(): TenantPartySecurityAttributes
    {
        $this->withDefaultUid();
        $this->withDefaultTenant();
        $this->withDefaultRelyingParty();
        $this->withDefaultPolicies();
        $this->withDefaultVersion();
        return $this;
    }
}
