<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\TenantPartySecurityRelyingPartyVO;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\RelyingPartyRef;

trait TenantPartySecurityRelyingPartyAccesor
{
    private TenantPartySecurityRelyingPartyVO $_relyingParty;
    public function getRelyingParty(): RelyingPartyRef
    {
        return $this->_relyingParty->value();
    }
    public function relyingParty(): TenantPartySecurityRelyingPartyVO
    {
        return $this->_relyingParty;
    }
    public function isRelyingPartyChanged(?self $original = null): bool
    {
        return $this->_relyingParty->value() !== ($original ? $original->_relyingParty->value() : null);
    }
    public function withRelyingParty(TenantPartySecurityRelyingPartyVO|RelyingPartyRef $relyingParty): self
    {
        $copy = clone $this;
        $copy->_relyingParty = TenantPartySecurityRelyingPartyVO::from($relyingParty);
        return $copy;
    }
}
