<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\TenantPartySecurityTenantVO;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;

trait TenantPartySecurityTenantAccesor
{
    private TenantPartySecurityTenantVO $_tenant;
    public function getTenant(): TenantRef
    {
        return $this->_tenant->value();
    }
    public function tenant(): TenantPartySecurityTenantVO
    {
        return $this->_tenant;
    }
    public function isTenantChanged(?self $original = null): bool
    {
        return $this->_tenant->value() !== ($original ? $original->_tenant->value() : null);
    }
    public function withTenant(TenantPartySecurityTenantVO|TenantRef $tenant): self
    {
        $copy = clone $this;
        $copy->_tenant = TenantPartySecurityTenantVO::from($tenant);
        return $copy;
    }
}
