<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Application\Usecase\Retrieve\TenantPartySecurityRetrieveUsecase;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Application\Usecase\Retrieve\TenantPartySecurityRetrieveResult;

class TenantPartySecurityRetrieveController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantPartySecurityRetrieveUsecase $retrieveUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/access/tenant-party-security{uid}",
        tags: ["Tenant party security"],
        description:"Create",
        parameters: [
               new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
          ],
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TenantPartySecurityApiDTO")),
              new OA\Response(response: 404, description: "Not found"),
          ]
    )]
    public function retrieve(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Retrieve Tenant party security");
        $span = $this->startSpan("Retrieve Tenant party security");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->retrieveUsecase->retrieve($uid);
            $value = $this->mapTenantPartySecurity($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapTenantPartySecurity(TenantPartySecurityRetrieveResult $value): TenantPartySecurityApiDTO
    {
        $this->logDebug("Map entity to output dto for Tenant party security");
        $span = $this->startSpan("Map entity to output dto for Tenant party security");
        try {
            $tenant = $value->getTenant();
            $relyingParty = $value->getRelyingParty();
            $dto = new TenantPartySecurityApiDTO();
            $dto->uid = $value->getUid();
            $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
            $dto->relyingParty = $relyingParty ? ['$ref' => $relyingParty->uid()] : null;
            $dto->policies = $value->getPolicies();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
