<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Application\Usecase\List;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Application\Service\Visibility\TenantTermsOfUseVisibilityService;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\Gateway\TenantTermsOfUseReadGateway;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\Gateway\TenantTermsOfUseFilter;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\Gateway\TenantTermsOfUseCursor;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\Gateway\TenantTermsOfUseAttributesSlide;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantTermsOfUseListUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantTermsOfUseVisibilityService $visibility,
        private readonly TenantTermsOfUseReadGateway $reader
    ) {
    }

    public function allowList(): Allow
    {
        $this->logDebug("Check allow of list usecase for Tenant terms of use");
        $span = $this->startSpan("Check allow of list usecase for Tenant terms of use");
        try {
            $result = $this->dispacher->dispatch(new TenantTermsOfUseListAllowDecision(Allow::allowed('list', 'Allowed to list by default')));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function count(TenantTermsOfUseFilter $filter): int
    {
        $this->logDebug("Run list usecase for Tenant terms of use");
        $span = $this->startSpan("Run list usecase for Tenant terms of use");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            return $this->visibility->countVisibles($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function list(TenantTermsOfUseFilter $filter, ?TenantTermsOfUseCursor $cursor = null): TenantTermsOfUseAttributesSlide
    {
        $this->logDebug("Run list usecase for Tenant terms of use");
        $span = $this->startSpan("Run list usecase for Tenant terms of use");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $slide = $this->visibility->listVisibles($filter, $cursor);
            return new TenantTermsOfUseAttributesSlide($slide->nextCursor(), array_map(fn ($item) => $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($item)), $slide->values()));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
