<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseEnabledVO;

trait TenantTermsOfUseEnabledAccesor
{
    private TenantTermsOfUseEnabledVO $_enabled;
    public function isEnabled(): bool
    {
        return $this->_enabled->value();
    }
    public function enabled(): TenantTermsOfUseEnabledVO
    {
        return $this->_enabled;
    }
    public function isEnabledChanged(?self $original = null): bool
    {
        return $this->_enabled->value() !== ($original ? $original->_enabled->value() : null);
    }
    public function withEnabled(TenantTermsOfUseEnabledVO|bool $enabled): self
    {
        $copy = clone $this;
        $copy->_enabled = TenantTermsOfUseEnabledVO::from($enabled);
        return $copy;
    }
}
