<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseTenantVO;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;

trait TenantTermsOfUseTenantAccesor
{
    private TenantTermsOfUseTenantVO $_tenant;
    public function getTenant(): TenantRef
    {
        return $this->_tenant->value();
    }
    public function tenant(): TenantTermsOfUseTenantVO
    {
        return $this->_tenant;
    }
    public function isTenantChanged(?self $original = null): bool
    {
        return $this->_tenant->value() !== ($original ? $original->_tenant->value() : null);
    }
    public function withTenant(TenantTermsOfUseTenantVO|TenantRef $tenant): self
    {
        $copy = clone $this;
        $copy->_tenant = TenantTermsOfUseTenantVO::from($tenant);
        return $copy;
    }
}
